const shuffle = (array) => {
  array.sort(() => Math.random() - 0.5);
};

const getDelayBaseOnDifficulty = (difficulty) => {
  if (difficulty >= 1 && difficulty <= 3) {
    return 2.5;
  } else if (difficulty >= 4 && difficulty <= 6) {
    return 2;
  } else if (difficulty >= 7 && difficulty <= 9) {
    return 1.5;
  } else {
    return 1;
  }
};

const getShootTypeBaseOnDifficulty = (difficulty) => {
  if (difficulty >= 1 && difficulty <= 2) {
    return { minNumber: 0, maxNumber: 2 };
  } else if (difficulty >= 3 && difficulty <= 7) {
    return { minNumber: 0, maxNumber: 3 };
  } else {
    return { minNumber: 0, maxNumber: 5 };
  }
};

const getPattern = (number, width, height) => {
  const timer = 0;
  const yIndex = [];

  if (number === 0) {
    timer = [0];
    yIndex = [width / 2];
  }

  return { timer, yIndex };
};

const createProductSpawnSystem = () => {
  let delayTime = 2;
  let pattern;
  let timer = 0;
  let index = 0;
  let yIndex = 0;
  return {
    update(delta, worldState, { width, height }) {
      const { difficulty, products } = worldState;

      console.log(products);
      if (worldState.stateTime > delayTime && pattern === undefined) {
        if (Math.random() > 0.99) {
          // single product
        } else {
          const { minNumber, maxNumber } = getShootTypeBaseOnDifficulty(
            difficulty
          );
          // pattern = getPattern(
          //   Math.round(Math.random() * (maxNumber - minNumber) + minNumber),
          //   width,
          //   height
          // );
          pattern = getPattern(0, width, height);
          timer = worldState.stateTime + pattern.timer[index];
        }
      }

      if (
        worldState.stateTime > delayTime &&
        worldState.stateTime > timer &&
        pattern !== undefined
      ) {
        index = Math.min((index += 1), pattern.timer.length);
        timer = worldState.stateTime + pattern.timer[index];

        let loop = 1;
        let tmpIndex = index - 1;
        while (true) {
          if (
            pattern.timer[tmpIndex] === pattern.timer[tmpIndex + 1] &&
            tmpIndex < pattern.timer.length
          ) {
            tmpIndex = Math.min((tmpIndex += 1), pattern.timer.length);
            loop += 1;
          } else {
            break;
          }
        }

        // add
        for (let i = 0; i < loop; i++) {
          if (i > 0) {
            index = Math.min((index += 1), pattern.timer.length);
          }
          timer = worldState.stateTime + pattern.timer[index];
          // get random product
          let randomProduct = 0;
          products.push({
            stateTime: 0,
            y: pattern.y[yIndex],
            type: randomProduct,
          });

          yIndex = Math.min((yIndex += 1), pattern.yIndex.length - 1);
        }

        if (index === pattern.timer.length) {
          delayTime =
            worldState.stateTime + getDelayBaseOnDifficulty(difficulty);
          index = 0;
          yIndex = 0;
          pattern = undefined;
        }
      }
    },
  };
};

export default createProductSpawnSystem;
