// import ReactDOM from "react-dom";
// import React from "react";
// import LeaderBoard from "../LeaderBoard";
// import axios from "../../utils/axios";

import { createViewportAwareInputHandler, Vector2 } from "gdxjs";
import createWorld from "../utils/createWorld";
import {
  getProductInBasketTex,
  getProductTexture,
  getFakeProductTex,
} from "../utils/getProduct";
import createLeaderBoardSystem from "./LeaderBoardScreen";
import createBackGroundRenderSystem from "../systems/BgRenderSystem";
import createBasketRenderSystem from "../systems/BasketRenderSystem";
import createInputHandlerGameScreenSystem from "../systems/InputHandlerGameScreenSystem";
import createBasketPositionSystem from "../systems/BasketPositionSystem";
import createProductSpawnSystem from "../systems/ProductSpawnSystem";
import createProductPositionSystem from "../systems/ProductPositionSystem";
import createProductRenderSystem from "../systems/ProductRenderSystem";
import createBasketCollideSystem from "../systems/BasketCollideSystem";
import createScoreCalcSystem from "../systems/ScoreCaculateSystem";
import createLifeRenderSystem from "../systems/LifeRenderSystem";
import createStarEffectRenderSystem from "../systems/StarEffectRenderSystem";

const createGameScreenSystem = (Game) => {
  const {
    width,
    height,
    canvas,
    gl,
    batch,
    camera,
    whiteTex,
    assets,
    viewport,
  } = Game;

  const inputHandler = createViewportAwareInputHandler(canvas, viewport);
  const BASKET_INITIAL_Y = (height * 3.65) / 4;

  const basketBack = assets.gameAssets.findRegion("basket_back", -1);
  const BASKET_WIDTH = width / 3.8;
  const BASKET_BACK_HEIGHT =
    (BASKET_WIDTH * basketBack.height) / basketBack.width;

  const productTex = getProductTexture(assets.gameAssets);
  const fakeProductTex = getFakeProductTex(assets.gameAssets);
  const productInBasketTex = getProductInBasketTex(assets.gameAssets);

  const worldState = {
    score: 0,
    correctCatch: false,
    stateTime: 0,
    gameOver: false,
    emptyLife: false,
    life: [{ open: true }, { open: true }, { open: true }],
    lifeIndex: 2, // life checking index
    basketState: {
      x: width / 2,
      y: BASKET_INITIAL_Y,
      width: BASKET_WIDTH,
      height: BASKET_BACK_HEIGHT,
      position: new Vector2(width / 2, BASKET_INITIAL_Y),
      target: new Vector2(width / 2, BASKET_INITIAL_Y),
      speed: width * 2.5,
    },
    difficulty: 1,
    collection: [
      { type: 0, enable: false },
      { type: 0, enable: false },
      { type: 1, enable: false },
    ],
    collectionFinished: [],
    products: [],
    productInfos: {
      accY: height / 2,
    },
    starList: [],
    whiteSmokeList: [],
    redSmokeList: [],
    stunScreen: { check: false, opacity: 1 },
  };

  const extra = {
    canvas,
    width,
    height,
    gl,
    batch,
    camera,
    whiteTex,
    inputHandler,
    assets,
    productTex,
    fakeProductTex,
    productInBasketTex,
  };
  const world = createWorld(worldState, extra);

  world.addSystem(createInputHandlerGameScreenSystem());
  world.addSystem(createBasketPositionSystem());
  world.addSystem(createProductSpawnSystem());
  world.addSystem(createProductPositionSystem());
  world.addSystem(createBasketCollideSystem());
  world.addSystem(createScoreCalcSystem());

  world.addSystem(createBackGroundRenderSystem());
  world.addSystem(createBasketRenderSystem());
  world.addSystem(createProductRenderSystem());
  world.addSystem(createLifeRenderSystem());
  world.addSystem(createStarEffectRenderSystem());

  const goToLeaderBoardScreen = async () => {
    Game.setCurrentScreen(await createLeaderBoardSystem(Game));
  };

  const update = (delta) => {
    if (!worldState.gameOver) {
      worldState.stateTime += delta;
      world.update(delta);
    } else {
      // world.update(delta, true);
      goToLeaderBoardScreen();
    }
  };

  return {
    update,
    destroy() {
      inputHandler.cleanup();
    },
  };
};

export default createGameScreenSystem;
