import { Vector2 } from "gdxjs";

const createBasketCollideSystem = () => {
  return {
    update(delta, worldState, { width, height }) {
      const { basketState, products, collection, life, starList } = worldState;

      let got = false;
      for (let i = products.length - 1; i >= 0; i--) {
        if (
          products[i].x > basketState.x - basketState.width / 2 &&
          products[i].x < basketState.x + basketState.width / 2 &&
          products[i].y > basketState.y - basketState.height * 0.7 &&
          products[i].y < basketState.y - basketState.height * 0.3
        ) {
          if (!products[i].isFake && !worldState.correctCatch) {
            console.log("hit");
            worldState.correctCatch = true;
            for (let j = 0; j < collection.length; j++) {
              if (
                collection[j].type === products[i].type &&
                !collection[j].enable &&
                !got
              ) {
                got = true;
                collection[j].enable = true;
              }
            }

            // 0: 1 star --- 1: 3 stars
            if (products[i].type === 2) {
              const time = 0.3;
              const speedX = basketState.width / time;
              const scaleSpeed = (1 - 0) / ((2 / 3) * time);
              starList.push({
                type: 0,
                stateTime: 0,
                time,
                scale: 0,
                scaleSpeed,
                speedX,
                accuX: 0,
                // value
              });
            } else {
              const stateTime = [0, 0, 0];
              const scale = [1, 1, 1];
              const time = [
                Math.random() * (0.4 - 0.3) + 0.3,
                Math.random() * (0.4 - 0.3) + 0.3,
                Math.random() * (0.4 - 0.3) + 0.3,
              ];
              const position = [
                new Vector2(basketState.x, basketState.y - basketState.height),
                new Vector2(basketState.x, basketState.y - basketState.height),
                new Vector2(basketState.x, basketState.y - basketState.height),
              ];
              const target = [
                new Vector2(
                  Math.random() * (width / 3),
                  Math.random() * ((height * 7) / 8 - (height * 6) / 8) +
                    (height * 6) / 8
                ),
                new Vector2(
                  Math.random() * ((width * 2) / 3 - width / 3) + width / 3,
                  Math.random() * ((height * 7) / 8 - (height * 6) / 8) +
                    (height * 6) / 8
                ),
                new Vector2(
                  Math.random() * (width - (width * 2) / 3) + (width * 2) / 3,
                  Math.random() * ((height * 7) / 8 - (height * 6) / 8) +
                    (height * 6) / 8
                ),
              ];

              let speed = [];
              let scaleSpeed = [];
              for (let i = 0; i < time.length; i++) {
                speed.push(target[i].distance(position[i]) / time[i]);
                scaleSpeed.push(1 / time[i]);
              }

              starList.push({
                type: 1,
                time,
                scale,
                stateTime,
                position,
                target,
                speed,
                scaleSpeed,
              });
            }

            products.splice(i, 1);
            break; // if there is any one match => break the loop
          }

          //wrong catch
          if (products[i].isFake) {
            console.log("miss");
            life[worldState.lifeIndex].open = false;
            worldState.lifeIndex = Math.max(0, (worldState.lifeIndex -= 1));
            worldState.stunScreen.check = true;
            products.splice(i, 1);
          }
        }
      }
    },
  };
};

export default createBasketCollideSystem;
