const createBasketCollideSystem = () => {
  return {
    update(delta, worldState, { width }) {
      const { basketState, products, collection, life, starList } = worldState;

      let got = false;
      for (let i = products.length - 1; i >= 0; i--) {
        if (
          products[i].x > basketState.x - basketState.width / 2 &&
          products[i].x < basketState.x + basketState.width / 2 &&
          products[i].y > basketState.y - basketState.height * 0.7 &&
          products[i].y < basketState.y - basketState.height * 0.6
        ) {
          if (!products[i].isFake && !worldState.correctCatch) {
            console.log("hit");
            worldState.correctCatch = true;
            for (let j = 0; j < collection.length; j++) {
              if (
                collection[j].type === products[i].type &&
                !collection[j].enable &&
                !got
              ) {
                got = true;
                collection[j].enable = true;
              }
            }

            // 0: 1 star --- 1: 3 stars
            if (products[i].type === 2) {
              const time = 1;
              const speedX = basketState.width / time;
              const scaleSpeed = (1 - 0) / (2 / 3);
              starList.push({
                stateTime: 0,
                time: 1,
                type: 0,
                scale: 0,
                scaleSpeed: scaleSpeed,
                speedX: speedX,
                accuX: 0,
                // value
              });
            } else {
              starList.push({
                type: 1,
                // value
              });
            }

            products.splice(i, 1);
            break; // if there is any one match => break the loop
          }

          //wrong catch
          if (products[i].isFake) {
            console.log("miss");
            life[worldState.lifeIndex].open = false;
            worldState.lifeIndex = Math.max(0, (worldState.lifeIndex -= 1));
            worldState.stunScreen.check = true;
            products.splice(i, 1);
          }
        }
      }
    },
  };
};

export default createBasketCollideSystem;
