const createStarEffectRenderSystem = () => {
  let startTex;
  let S1_WIDTH, S1_HEIGHT;

  return {
    init(worldState, { assets, width, height }) {
      const atlas = assets.gameAssets;
      startTex = atlas.findRegion("star", -1);

      S1_WIDTH = width / 10;
      S1_HEIGHT = (S1_WIDTH * startTex.height) / startTex.width;
    },
    update(delta, { starList, basketState }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();

      for (let i = starList.length - 1; i >= 0; i--) {
        if (starList[i].type === 0) {
          starList[i].stateTime += delta;
          starList[i].accuX += delta * starList[i].speedX;
          if (starList[i].accuX < (basketState.width * 2) / 3) {
            starList[i].scale += delta * starList[i].scaleSpeed;
          } else {
            starList[i].scale -= delta * starList[i].scaleSpeed * 2;
          }
          startTex.draw(
            batch,
            basketState.x + starList[i].accuX - S1_WIDTH / 2,
            basketState.y - S1_HEIGHT / 2,
            S1_WIDTH,
            S1_HEIGHT,
            S1_WIDTH / 2,
            S1_HEIGHT / 2,
            0,
            starList[i].scale,
            starList[i].scale
          );

          if (starList[i].stateTime > starList[i].time) {
            starList.splice(i, 1);
          }
        }
      }

      batch.end();
    },
  };
};

export default createStarEffectRenderSystem;
