const createBasketRenderSystem = () => {
  let basketFront, basketBack;
  return {
    init({ basketState }, { width, assets }) {
      let atlas = assets.gameAssets;
      basketFront = atlas.findRegion("basket_front", -1);
      basketBack = atlas.findRegion("basket_back", -1);
    },

    update(delta, { basketState }, { batch, width, height, camera }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(1, 1, 1, 1);

      basketFront.draw(
        batch,
        basketState.x - basketState.width / 2,
        basketState.y,
        basketState.width,
        basketState.height
      );

      basketBack.draw(
        batch,
        basketState.x - basketState.width / 2,
        basketState.y,
        basketState.width,
        basketState.height
      );

      batch.end();
    },
  };
};

export default createBasketRenderSystem;
