// import ReactDOM from "react-dom";
// import React from "react";
// import LeaderBoard from "../LeaderBoard";
// import axios from "../../utils/axios";

import { createViewportAwareInputHandler, Vector2 } from "gdxjs";
import createWorld from "../utils/createWorld";
import {
  getProductInBasketTex,
  getProductTexture,
  getFakeProductTex,
} from "../utils/getProduct";
import createLeaderBoardSystem from "./LeaderBoardScreen";
import createBackGroundRenderSystem from "../systems/BgRenderSystem";

const createGameScreenSystem = (Game) => {
  const {
    width,
    height,
    canvas,
    gl,
    batch,
    camera,
    whiteTex,
    assets,
    viewport,
  } = Game;

  const inputHandler = createViewportAwareInputHandler(canvas, viewport);
  const TRAY_INITIAL_Y = (height * 3.1) / 4;

  const BASKET_TEX = assets.gameAssets.findRegion("basket_back", -1);
  const BASKET_WIDTH = width / 3.8;
  const BASKET_HEIGHT = (BASKET_WIDTH * BASKET_TEX.height) / BASKET_WIDTH;

  const productTex = getProductTexture(assets.gameAssets);
  const fakeProductTex = getFakeProductTex(assets.gameAssets);
  const productInBasketTex = getProductInBasketTex(assets.gameAssets);

  const worldState = {
    score: 0, // keep
    correctCatch: false, // keep
    stateTime: 0, // keep
    gameOver: false, // keep
    emptyLife: false, // keep
    life: [{ open: true }, { open: true }, { open: true }], // keep
    lifeIndex: 2, // life checking index // keep
    basketState: {
      x: width / 2,
      y: TRAY_INITIAL_Y,
      width: BASKET_WIDTH,
      height: BASKET_HEIGHT,
      position: new Vector2(width / 2, TRAY_INITIAL_Y),
      target: new Vector2(width / 2, TRAY_INITIAL_Y),
      moveLeft: false,
      moveRight: false,
      speed: width * 2.5,
    },
    difficulty: 1, // keep
    collection: [], // keep
    collectionFinished: [], // keep
    products: [], // keep
  };

  const extra = {
    canvas,
    width,
    height,
    gl,
    batch,
    camera,
    whiteTex,
    inputHandler,
    assets,
    productTex,
    fakeProductTex,
    productInBasketTex,
  };
  const world = createWorld(worldState, extra);

  world.addSystem(createBackGroundRenderSystem());
  world.addSystem(createBasketRenderSystem());

  const goToLeaderBoardScreen = async () => {
    Game.setCurrentScreen(await createLeaderBoardSystem(Game));
  };

  const update = (delta) => {
    if (!worldState.gameOver) {
      worldState.stateTime += delta;
      world.update(delta);
    } else {
      // world.update(delta, true);
      goToLeaderBoardScreen();
    }
  };

  return {
    update,
    destroy() {
      inputHandler.cleanup();
    },
  };
};

export default createGameScreenSystem;
