const createBasketRenderSystem = () => {
  let basketFront, basketBack;
  let F_HEIGHT, B_HEIGHT;
  let productInfos = [];
  return {
    init({ basketState }, { width, assets, productInBasketTex }) {
      let atlas = assets.gameAssets;
      basketFront = atlas.findRegion("basket_front", -1);
      basketBack = atlas.findRegion("basket_back", -1);

      F_HEIGHT = (basketState.width * basketFront.height) / basketFront.width;
      B_HEIGHT = (basketState.width * basketBack.height) / basketBack.width;

      // collection infos
      const P_WIDTH = width / 9;

      for (let i = 0; i < productInBasketTex; i++) {
        const P_HEIGHT =
          (P_WIDTH * productInBasketTex[i].image.height) /
          productInBasketTex[i].image.width;

        productInfos.push({
          tex: productInBasketTex[i].image,
          width: P_WIDTH,
          height: P_HEIGHT,
        });
      }
    },

    update(
      delta,
      { basketState, collection },
      { batch, width, height, camera }
    ) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(1, 1, 1, 1);

      basketBack.draw(
        batch,
        basketState.x - basketState.width / 2,
        basketState.y - B_HEIGHT,
        basketState.width,
        B_HEIGHT
      );

      console.log(productInfos);
      // draw collection
      for (let i = 0; i < collection.length; i++) {
        if (collection[i].enable) {
          productInfos[i].tex.draw(
            batch,
            basketState.x + basketState.width * 0.1,
            basketState.y - productInfos[i].height,
            productInfos[i].width,
            productInfos[i].height
          );
        }
      }

      basketFront.draw(
        batch,
        basketState.x - basketState.width / 2,
        basketState.y - F_HEIGHT,
        basketState.width,
        F_HEIGHT
      );

      batch.end();
    },
  };
};

export default createBasketRenderSystem;
