import { Vector2 } from "gdxjs";

const createInputHandlerGameScreenSystem = () => {
  let worldCoord = new Vector2();

  return {
    init({ basketState }, { inputHandler, width }) {
      inputHandler.addEventListener("touchStart", () => {
        worldCoord.setVector(inputHandler.getTouchedWorldCoord());
        basketState.target.x = worldCoord.x;
      });

      inputHandler.addEventListener("touchMove", () => {
        worldCoord.setVector(inputHandler.getTouchedWorldCoord());
        basketState.target.x = worldCoord.x;
      });
    },
    update(delta, { trayState }, { inputHandler }) {
      // if (inputHandler.getTouchedWorldCoord()) {
      //   worldCoord.setVector(inputHandler.getTouchedWorldCoord());
      //   trayState.target.x = worldCoord.x;
      // }
    },
  };
};
export default createInputHandlerGameScreenSystem;
