const createProductRenderSystem = () => {
  let P_HEIGHT;
  let productInfos = [];
  let fakeProductInfos = [];
  let product;
  return {
    init(worldState, { width, height, productTex, fakeProductTex }) {
      P_HEIGHT = height / 10;
      for (let i = 0; i < productTex.length; i++) {
        productInfos.push({
          tex: productTex[i].image,
          width:
            (productTex[i].image.width * P_HEIGHT) / productTex[i].image.height,
        });
      }

      for (let i = 0; i < fakeProductTex.length; i++) {
        fakeProductInfos.push({
          tex: fakeProductTex[i].image,
          width:
            (fakeProductTex[i].image.width * P_HEIGHT) /
            fakeProductTex[i].image.height,
        });
      }
    },
    update(
      delta,
      { products },
      { batch, camera, width, height, productTex, fakeProductTex }
    ) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(1, 1, 1, 1);
      for (let i = products.length - 1; i >= 0; i--) {
        product = !products[i].isFake
          ? productInfos[products[i].type]
          : fakeProductInfos[products[i].type];

        product.tex.draw(
          batch,
          products[i].x - product.width / 2,
          products[i].y,
          product.width,
          P_HEIGHT
        );
      }
      batch.end();
    },
  };
};

export default createProductRenderSystem;
