import { createViewportAwareInputHandler, fillRect, Vector2 } from "gdxjs";
import createGameScreenSystem from "./GameScreen";
import { checkPointInsideRect } from "../utils/coordUtils";

const createInstructionScreen2System = (Game) => {
  const goToGameScreen = async () => {
    Game.setCurrentScreen(await createGameScreenSystem(Game));
  };

  const { canvas, viewport, width, height, camera, batch, assets } = Game;

  const inputHandler = createViewportAwareInputHandler(canvas, viewport);

  const atlas = assets.startScreen;
  const bg = assets.backGround;

  // const button = atlas.findRegion("button-play", -1);
  // const banner1 = atlas.findRegion("hung_nguyen_lieu_de_ghi_diem", -1);
  // const banner2 = atlas.findRegion(
  //   "ne_cac_nguyen_lieu_khong_co_trong_menu",
  //   -1
  // );

  // const B1_WIDTH = width * 0.9;
  // const B1_HEIGHT = (B1_WIDTH * banner1.height) / banner1.width;

  // const B2_WIDTH = width * 0.9;
  // const B2_HEIGHT = (B2_WIDTH * banner2.height) / banner2.width;

  // const BUTTON_WIDTH = width / 3.5;
  // const BUTTON_HEIGHT = (BUTTON_WIDTH * button.height) / button.width;

  // const buttonInfos = [
  //   width / 2 - BUTTON_WIDTH / 2,
  //   (height * 2.6) / 3,
  //   BUTTON_WIDTH,
  //   BUTTON_HEIGHT,
  // ];

  const coord = new Vector2();
  let point;
  inputHandler.addEventListener("touchStart", (x, y) => {
    coord.setVector(inputHandler.getTouchedWorldCoord());
    point = { x: coord.x, y: coord.y };
    if (checkPointInsideRect(point, buttonInfos)) {
      goToGameScreen();
    }
  });

  const update = (delta) => {
    batch.setProjection(camera.combined);
    batch.begin();
    batch.setColor(1, 1, 1, 1);
    fillRect(batch, bg, 0, 0, width, height);
    banner1.draw(
      batch,
      width / 2 - B1_WIDTH / 2,
      height / 16,
      B1_WIDTH,
      B1_HEIGHT
    );

    banner2.draw(
      batch,
      width / 2 - B2_WIDTH / 2,
      (height * 1.4) / 3,
      B2_WIDTH,
      B2_HEIGHT
    );

    button.draw(
      batch,
      buttonInfos[0],
      buttonInfos[1],
      buttonInfos[2],
      buttonInfos[3]
    );
    batch.end();
  };

  return {
    update,
    destroy() {
      inputHandler.cleanup();
    },
  };
};

export default createInstructionScreen2System;
