const createVitaminARenderSystem = () => {
  let V_WIDTH, V_HEIGHT, vitaminTex;
  return {
    init(worldState, { productTex, width, height }) {
      vitaminTex = productTex[2].image;
      V_WIDTH = width / 15;
      V_HEIGHT = (V_WIDTH * vitaminTex.height) / vitaminTex.width;
    },
    update(delta, { vitaminACounter }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();
      for (let i = 0; i < vitaminACounter; i++) {
        vitaminTex.draw(
          batch,
          width * 0.84,
          height / 13 + i * (height / 70 + V_HEIGHT),
          V_WIDTH,
          V_HEIGHT
        );
      }
      batch.end();
    },
  };
};

export default createVitaminARenderSystem;
