import { AlignMode, TextType } from "../utils/loadFontFile";

const createScoreRenderSystem = () => {
  let scoreText;

  return {
    init(worldState, { font, width, height }) {
      console.log(font);
      scoreText = font.setNewText(width);
      scoreText.setAlignMode(AlignMode.left);
      scoreText.setType(TextType.dynamic);
    },
    update(delta, { score }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(0.502, 0.867, 0.216, 1);
      scoreText.draw(batch, `${score}`, width / 3.2, height / 10, width / 9.3);
      // batch.setColor(1, 1, 1, 1);
      batch.end();
    },
  };
};

export default createScoreRenderSystem;
