const createLifeRenderSystem = () => {
  let openEye, closeEye;
  let L_WIDTH, L_HEIGHT;
  return {
    init(worldState, { width, height, assets }) {
      const atlas = assets.gameAssets;

      openEye = atlas.findRegion("open_eye", -1);
      closeEye = atlas.findRegion("close_Eye", -1);

      L_WIDTH = width / 11;
      L_HEIGHT = (L_WIDTH * openEye.height) / openEye.width;
    },
    update(delta, { life }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();
      for (let i = 0; i < life.length; i++) {
        let lifeTex = closeEye;
        if (life[i].open) {
          lifeTex = openEye;
        }
        lifeTex.draw(
          batch,
          width / 1.6 + i * (L_WIDTH + width / 50),
          height / 19,
          L_WIDTH,
          L_HEIGHT
        );
      }
      batch.end();
    },
  };
};

export default createLifeRenderSystem;
