const createVitaminARenderSystem = () => {
  let V_WIDTH, V_HEIGHT, vitaminTex;
  return {
    init(worldState, { productTex, width, height }) {
      vitaminTex = productTex[2].image;
      V_WIDTH = width / 15;
      V_HEIGHT = (V_WIDTH * vitaminTex.height) / vitaminTex.width;
    },
    update(delta, worldState, { batch, camera, width, height }) {
      const life = worldState;
      batch.setProjection(camera.combined);
      batch.begin();
      for (let i = 0; i < worldState.vitaminACounter; i++) {
        vitaminTex.draw(
          batch,
          width * 0.87,
          height / 8 + i * (height / 60 + V_HEIGHT),
          V_WIDTH,
          V_HEIGHT
        );
      }
      batch.end();

      if (worldState.vitaminACounter === 5) {
        for (let i = life.length - 1; i >= 0; i--) {
          if (!life[i].open) {
            life[i].open = true;
            break;
          }
        }
        worldState.vitaminACounter = 0;
      }
    },
  };
};

export default createVitaminARenderSystem;
