import { createViewportAwareInputHandler, fillRect, Vector2 } from "gdxjs";
import createGameScreenSystem from "./GameScreen";
import { checkPointInsideRect } from "../utils/coordUtils";

const createInstructionScreen2System = (Game) => {
  const goToGameScreen = async () => {
    Game.setCurrentScreen(await createGameScreenSystem(Game));
  };

  const {
    canvas,
    viewport,
    width,
    height,
    camera,
    batch,
    assets,
    whiteTex,
  } = Game;

  const inputHandler = createViewportAwareInputHandler(canvas, viewport);

  const atlas = assets.gameAssets;

  const backGround = atlas.findRegion("BG", -1);
  const BG_HEIGHT = height;
  const BG_WIDTH = (BG_HEIGHT * backGround.width) / backGround.height;
  const panel1 = atlas.findRegion("panel", -1);
  const PANEL_WIDTH = width * 0.78;
  const PANEL_HEIGHT = (PANEL_WIDTH * panel1.height) / panel1.width;
  const border = atlas.findRegion("border", -1);
  const B_HEIGHT = height;
  const B_WIDTH = (B_HEIGHT * border.width) / border.height;

  inputHandler.addEventListener("touchStart", (x, y) => {
    goToInstructionScreen2();
  });

  const update = (delta) => {
    batch.setProjection(camera.combined);
    batch.begin();
    batch.setColor(0.039, 0.988, 0.114, 1);
    fillRect(batch, whiteTex, 0, 0, width, height);
    batch.setColor(1, 1, 1, 1);
    backGround.draw(batch, width / 2 - BG_WIDTH / 2, 0, BG_WIDTH, BG_HEIGHT);
    panel1.draw(
      batch,
      width / 2 - PANEL_WIDTH / 2,
      (height - PANEL_HEIGHT) / 2,
      PANEL_WIDTH,
      PANEL_HEIGHT
    );
    border.draw(batch, 0, 0, B_WIDTH, B_HEIGHT);
    border.draw(batch, width - B_WIDTH, 0, B_WIDTH, B_HEIGHT);

    batch.end();
  };

  return {
    update,
    destroy() {
      inputHandler.cleanup();
    },
  };
};

export default createInstructionScreen2System;
