const createBackGroundRenderSystem = () => {
  let bg, border;
  let BG_WIDTH, BG_HEIGHT, B_WIDTH, B_HEIGHT;

  return {
    init(worldState, { width, height, assets }) {
      let atlas = assets.gameAssets;

      bg = atlas.findRegion("BG", -1);
      border = atlas.findRegion("border", -1);

      BG_HEIGHT = height;
      BG_WIDTH = (BG_HEIGHT * bg.width) / bg.height;

      B_HEIGHT = height;
      B_WIDTH = (B_HEIGHT * border.width) / border.height;
    },
    update(delta, worldState, { batch, width, height, camera }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(1, 1, 1, 1);
      bg.draw(batch, width / 2 - BG_WIDTH / 2, 0, BG_WIDTH, BG_HEIGHT);

      border.draw(batch, 0, 0, B_WIDTH, B_HEIGHT);
      border.draw(batch, width - B_WIDTH, 0, B_WIDTH, B_HEIGHT);

      batch.end();
    },
  };
};

export default createBackGroundRenderSystem;
