import { createAnimation } from "gdxjs";

const createWhiteSmokeRenderSystem = () => {
  let smokeAnimation;
  const duration = 0.05;
  let frameLength = 0;
  let SMOKE_WIDTH, SMOKE_HEIGHT;
  return {
    init(delta, worldState, { assets, width, height }) {
      const smokeAtlas = assets.smoke;
      const effect = smokeAtlas.findRegions("effect boc khoi");
      smokeAnimation = createAnimation(duration, effect);
      frameLength = effect.length - 1;

      SMOKE_WIDTH = width / 6;
      SMOKE_HEIGHT = (effect[0].height * SMOKE_WIDTH) / effect[0].width;
    },
    update(delta, { whiteSmokeList }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setcolor(1, 1, 1, 1);
      for (let i = whiteSmokeList.length - 1; i >= 0; i--) {
        whiteSmokeList[i].stateTime += delta;
        smokeAnimation
          .getKeyFrame(whiteSmokeList[i].stateTime, 1)
          .draw(
            batch,
            whiteSmokeList[i].x - SMOKE_WIDTH / 2.5,
            whiteSmokeList[i].y - SMOKE_HEIGHT / 3,
            SMOKE_WIDTH,
            SMOKE_HEIGHT
          );

        if (whiteSmokeList[i].stateTime > duration * frameLength) {
          whiteSmokeList.splice(i, 1);
        }
      }
      batch.end();
    },
  };
};

export default createWhiteSmokeRenderSystem;
