const createProductPositionSystem = () => {
  return {
    update(delta, { products, productInfos }, { height }) {
      for (let i = products.length - 1; i >= 0; i--) {
        products[i].y += delta * products[i].speedY;
        products[i].speedY += productInfos.accY * delta;

        if (products[i].y > height * 0.9) {
          products.splice(i, 1);
        }
      }
    },
  };
};

export default createProductPositionSystem;
