import { Vector2 } from "gdxjs";

const createStarEffectRenderSystem = () => {
  let startTex;
  let S1_WIDTH, S1_HEIGHT;
  let tmp = new Vector2();
  return {
    init(worldState, { assets, width, height }) {
      const atlas = assets.gameAssets;
      startTex = atlas.findRegion("star", -1);

      S1_WIDTH = width / 8;
      S1_HEIGHT = (S1_WIDTH * startTex.height) / startTex.width;
    },
    update(delta, { starList, basketState }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();

      for (let i = starList.length - 1; i >= 0; i--) {
        if (starList[i].type === 0) {
          starList[i].stateTime += delta;
          starList[i].accuX += delta * starList[i].speedX;
          if (starList[i].accuX < (basketState.width * 2) / 3) {
            starList[i].scale += delta * starList[i].scaleSpeed;
          } else {
            starList[i].scale -= delta * starList[i].scaleSpeed * 1.5;
          }
          startTex.draw(
            batch,
            basketState.x -
              basketState.width / 2 +
              starList[i].accuX -
              S1_WIDTH / 2,
            basketState.y - basketState.height * 0.7 - S1_HEIGHT / 2,
            S1_WIDTH,
            S1_HEIGHT,
            S1_WIDTH / 2,
            S1_HEIGHT / 2,
            0,
            starList[i].scale,
            starList[i].scale
          );

          if (starList[i].stateTime > starList[i].time) {
            starList.splice(i, 1);
          }
        } else {
          for (let j = 2; j >= 0; j--) {
            console.log(starList[i].stateTime[2]);
            if (starList[i].stateTime[j] <= starList[i].time[j]) {
              starList[i].stateTime[j] += delta;
              const target = starList[i].target[j];
              const position = starList[i].position;
              tmp
                .setVector(target)
                .subVector(position)
                .nor()
                .scale(starList[i].speed[j] * delta);
              if (target.distanceSqr(position) <= tmp.len2()) {
                starList[i].position.setVector(starList[i].target[j]);
              } else {
                starList[i].position.addVector(tmp);
              }

              startTex.draw(
                batch,
                starList[i].position.x,
                starList[i].position.y,
                S1_WIDTH,
                S1_HEIGHT
                // S1_WIDTH / 2,
                // S1_HEIGHT / 2,
                // 0,
                // starList[i].scale,
                // starList[i].scale
              );
            } else {
              console.log("dcm");
            }
          }
        }
      }

      batch.end();
    },
  };
};

export default createStarEffectRenderSystem;
