export const getProductInBasketTex = (atlas) => {
  const productTex = [
    {
      image: atlas.findRegion("lo_1_gio", -1),
      type: 0,
    },
    {
      image: atlas.findRegion("lo_2_gio", -1),
      type: 0,
    },
    {
      image: atlas.findRegion("hop_thuoc_gio", -1),
      type: 1,
    },
  ];
  return productTex;
};

export const getProductTexture = (atlas) => {
  const productTex = [
    {
      image: atlas.findRegion("lo_thuoc", -1),
      type: 0,
    },
    {
      image: atlas.findRegion("hop_thuoc", -1),
      type: 1,
    },
    {
      image: atlas.findRegion("vitaminA", -1),
      type: 2,
    },
  ];
  return productTex;
};

export const getFakeProductTex = (atlas) => {
  const productTex = [
    {
      image: atlas.findRegion("lo_thuoc_fake", -1),
      type: 0,
    },
    {
      image: atlas.findRegion("lo_thuoc_fake2", -1),
      type: 1,
    },
    {
      image: atlas.findRegion("hop_thuoc_fake", -1),
      type: 2,
    },
    {
      image: atlas.findRegion("hop_thuoc_fake2", -1),
      type: 3,
    },
  ];
  return productTex;
};
